CREATE TABLE IF NOT EXISTS accounts (userID VARCHAR(50) NOT NULL,PRIMARY KEY (userID));CREATE TABLE IF NOT EXISTS rssfeeds (feedID INT NOT NULL AUTO_INCREMENT,FeedLink VARCHAR(255) NOT NULL,PRIMARY KEY (feedID));CREATE TABLE IF NOT EXISTS accountfeeds (userID VARCHAR(50) NOT NULL,feedID INT NOT NULL,FeedFriendlyName VARCHAR(255) NOT NULL,FOREIGN KEY (userID) REFERENCES accounts(userID),FOREIGN KEY (feedID) REFERENCES rssfeeds(feedID));